export default function AdventureNavbar() {
  return (
    <div className="  w-full bg-white z-[9999]">
      <nav className="flex items-center justify-between px-6 py-4 bg-transparent">
        <div className="flex-shrink-0 h-20 w-52 relative">
          <Link href="/" legacyBehavior passHref>
            <Image
              alt="extreme adventure"
              src="/logo/logo.svg"
              fill
              objectFit="contain"
            />
          </Link>
        </div>

        {/* Center: Navigation Menu */}
        <div className="flex-1 flex justify-center">
          <NavigationMenu>
            <NavigationMenuList className="flex space-x-6">
              {menuItems.map(({ title, href, subcategories }) => (
                <NavigationMenuItem key={title}>
                  {subcategories && subcategories.length > 0 ? (
                    <>
                      <NavigationMenuTrigger>{title}</NavigationMenuTrigger>
                      <NavigationMenuContent>
                        <ul className="grid gap-3 p-4 w-[300px] md:w-[400px]">
                          {subcategories.map((sub) => (
                            <ListItem
                              key={sub.title}
                              href={sub.href}
                              title={sub.title}
                            >
                              <p className="text-sm leading-tight text-muted-foreground"></p>
                            </ListItem>
                          ))}
                        </ul>
                      </NavigationMenuContent>
                    </>
                  ) : (
                    <Link href={href || "#"} legacyBehavior passHref>
                      <NavigationMenuLink
                        className={navigationMenuTriggerStyle()}
                      >
                        {title}
                      </NavigationMenuLink>
                    </Link>
                  )}
                </NavigationMenuItem>
              ))}
            </NavigationMenuList>
          </NavigationMenu>
        </div>

        {/* Right: Empty or add other items here */}
        <div className="flex-shrink-0">
          <Button className="rounded-full">Get an adventure</Button>
        </div>
      </nav>
    </div>
  );
}

const ListItem = React.forwardRef<
  React.ElementRef<"a">,
  React.ComponentPropsWithoutRef<"a">
>(({ className, title, children, ...props }, ref) => {
  return (
    <li>
      <NavigationMenuLink asChild>
        <a
          ref={ref}
          className={cn(
            "block select-none rounded-md p-3 leading-none no-underline outline-none transition-colors hover:bg-accent hover:text-accent-foreground focus:bg-accent focus:text-accent-foreground",
            className
          )}
          {...props}
        >
          <div className="text-sm font-medium leading-none">{title}</div>
          {children && (
            <p className="line-clamp-2 text-sm leading-snug text-muted-foreground">
              {children}
            </p>
          )}
        </a>
      </NavigationMenuLink>
    </li>
  );
});
ListItem.displayName = "ListItem";
